        ,T0.prm_mgr_id --主管户客户经理工号
        ,T0.prm_mgr_nm --主管户客户经理名称
        ,T0.tem_org_id --团队机构号
        ,T0.tem_org_nm --团队名称
        ,T0.sbr_org_nm --支行名称
        ,T0.brc_org_nm --分行名称
