ON      T1.CTR_SERNO = T22.CTR_SERNO
WHERE   T2.LOAN_CST_TYP_CD in ( '04','08','09') --工薪族
--剔除员工贷款、重组贷款或者包含没有合同
AND     ( (  t1.BSN_MARK_CD NOT IN ( '01' )
