                         ,sum(ctr_amt * intr_rat * duration) ctr_fenzi
                         ,sum(ctr_amt * duration) ctr_fenmu
                         ,sum(dbil_fenzi) dbil_fenzi
                         ,sum(dbil_fenmu) dbil_fenmu
