		,t.close_date --关闭日期
		,t.org_type --机构类型
        --是否续贷
        ,CASE max(是否续贷)
           WHEN 2 THEN '合同未到期'
           WHEN 1 THEN '续贷'
           WHEN 0 THEN '--'
         END AS 是否续贷标签
        --续贷担保变化
        ,CASE
           WHEN max(续贷担保变化) = 4 THEN '担保不变'
           WHEN max(续贷担保变化) = 3 THEN '担保变弱'
           WHEN max(续贷担保变化) = 2 THEN '担保变强'
           WHEN max(续贷担保变化) = 1 THEN '合同未到期'
           WHEN max(续贷担保变化) = 0 THEN '--'
         END AS 续贷担保变化标签
		 ,t.ACT_ID
--4担保不变 3担保变弱 2担保变强 1合同未到期 0--
FROM    xudai_zx_danbao t
GROUP BY 是否续贷 , 续贷担保变化, t.REPORT_ID
		,t.act_typ_cd --账户类型
		,t.ctr_amt  --贷款金额
		,t.dtrb_org_code --机构代码
		,t.act_pd_cd  --产品
		,t.act_start_dt --开立日期
		,t.grnt_mth_cd --担保方式
		,t.close_date --关闭日期
		,t.org_type --机构类型
		,t.ACT_ID
;
