        ,BUSSUP_INSTYPE                                                                                                        AS ORG_TYPE
        ,SUBSTR(REPLACE(OPEN_DATE, '-', ''), 1, 8)                                                                             AS OPEN_DATE
        ,IF(SUBSTR(REPLACE(EXPIRE_DATE, '-', ''), 1, 8) = '20991231', '99991231', SUBSTR(REPLACE(EXPIRE_DATE, '-', ''), 1, 8)) AS DUE_DATE
