FROM    (
            SELECT  DISTINCT A1.REPORT_ID --报告编号
                             ,A1.CREDIT_NUMBER --授信协议编号
                             ,COALESCE(A1.CREDIT_LINE_RMB, A1.LOAN_AMOUNT_RMB) AS CTR_AMT --授信额度_折人民币|借款金额_折人民币
            FROM    edw.NCIP_CE_M_ACCOUNT_INFO A1
