         END       AS BE_RCVRY_ACT_NUM -- 被追偿账户数
        ,CASE
           WHEN ACT_PD_CD IN ( 'A1' , 'B1' ) THEN BAL
           ELSE 0
         END       AS BE_RCVRY_ACT_BAL -- 被追偿账户余额
        ,CASE
           WHEN ACC_STATUS_TYPE = '4' THEN 1
           ELSE 0
         END       AS BAD_DBT_ACT_NUM --呆账账户数
        ,CASE
