                                  FROM    lab_bigdata_dev.TMP_ADM_IND_I_CRDT_RPT_RSK_IDX_DD_03_8
                                  GROUP BY REPORT_ID
                              ) T3
                 ON      T1.REPORT_NO = T3.REPORT_ID
                 GROUP BY T1.CST_ID
