ON      A.CST_ID = F.CST_ID
LEFT JOIN    (
                 SELECT  T1.CST_ID
                         ,SUM(NVL(T2.CTR_AMT, 0) + NVL(T3.CTR_AMT, 0)) AS CST_CTR_AMT
                 FROM    lab_bigdata_dev.TMP_ADM_IND_I_CRDT_RPT_RSK_IDX_DD_01 T1
