                                ELSE datediff(to_date(T1.END_DT, 'yyyymmdd'), to_date(dtrb_dt, 'yyyymmdd'), 'dd')
                              END, 0) 用信时间
                 FROM    EDW.DIM_BUS_LOAN_DBIL_INF_DD T1
