            ELSE t.amt * ( DATEDIFF(to_date(t.apnt_mtu_day, 'yyyymmdd'), to_date(t.dtrb_dt, 'yyyymmdd'), 'dd') + 1 ) * t.exe_intr_rat
          END) / SUM(CASE
                      WHEN t.end_dt <> '18991231' THEN t.amt * ( DATEDIFF(to_date(t.end_dt, 'yyyymmdd'), to_date(t.dtrb_dt, 'yyyymmdd'), 'dd') + 1 )
