           WHEN t2.pd_cd = '31' THEN '个人助学贷款'
           WHEN t2.pd_cd = '32' THEN '国家助学贷款'
           WHEN t2.pd_cd = '33' THEN '商业助学贷款'
           WHEN t2.pd_cd = '41' THEN '个人经营性贷款'
           WHEN t2.pd_cd = '42' THEN '个人创业担保贷款'
           WHEN t2.pd_cd = '51' THEN '农户贷款'
