        ,t2.cur_mon_actl_rpay --本月实还款
        ,t2.act_sts_cd --账户状态
        ,t2.dtrb_org_typ_cd
        ,CASE t2.dtrb_org_typ_cd
           WHEN '11' THEN '商业银行'
           WHEN '12' THEN '村镇银行'
           WHEN '14' THEN '住房储蓄银行'
           WHEN '15' THEN '外资银行'
           WHEN '16' THEN '财务公司'
           WHEN '21' THEN '信托公司'
