				, max(ctr_bgn_dt)  ctr_bgn_dt
				,sum(ctr_amt)  ctr_amt
				,sum(ctr_bal)  ctr_bal
			from edw.DIM_BUS_LOAN_CTR_BSE_INF_DD
			where dt='20241013'
			and  PRD_NO not in ( '2002010101001','2002010101901')
			GROUP BY  cst_id
			having min(ctr_bgn_dt)>='20240531'
			) t3   --剔除信用卡
on t2.cst_id=t3.cst_id
