                                             WHEN intr_rat >1 AND dtrb_org_typ_cd NOT IN ( '商业银行' , '村镇银行' , '住房储蓄银行' , '外资银行' ) THEN ctr_amt * intr_rat
                                             ELSE 0
                                           END) / SUM(CASE
                                                        WHEN intr_rat >1 AND dtrb_org_typ_cd NOT IN ( '商业银行' , '村镇银行' , '住房储蓄银行' , '外资银行' ) THEN ctr_amt
                                                        ELSE 0
                                                      END), 2)
                          END                 AS 非银机构贷款加权利率
                 FROM    lab_bigdata_dev.max_report_0715
                 WHERE   dtrb_org <> '泰隆'
                 AND     cst_id <> ''
