LEFT JOIN    (
                 SELECT  TT4.cst_id
                         ,SUM(NVL(TT4.mon_acm_prcp_bal_acml * TT5.avg_prc / 100, 0)) / substr(${yyyy-1-1231}, 7, 2) AS mon_acm_prcp_bal_acml_avg
                 FROM    edw.DIM_BUS_LOAN_CTR_BSE_INF_DD TT3   ----信贷合同信息表
