 ( T1.CTR_STS_CD IN ( '2' , '4' ) AND T1.TMT_DT = '18991231' AND to_date(T1.CTR_MTU_DT, 'yyyyMMdd') >= to_date(T1.dt, 'yyyyMMdd') )
