        DATETRUNC(to_date(t.dt, 'yyyymmdd'), 'yyyy'), 'dd')   doy --年度天数
 FROM (select dt, src_act_id, acs_org_cd, pd_cd, itm_cd, dat_src_cd, ccy_cd,
              cur_bal,
              mon_acm_bal, intr_inc_xps_mon_acm, fnl_ftp_intr_mon_acm,
