CREATE TABLE LAB_BIGDATA_DEV.HGL_ORG_TAG_IND_DD_2_01_ZHJY_07_QT as
SELECT  t1.org_id
        ,t1.ZHJY2_0014_01
        ,t1.ZHJY2_0014_02
        ,t1.ZHJY2_0014
        ,t1.ZHJY2_0015_01
        ,t1.ZHJY2_0015
        ,t1.ZHJY2_0016_01
        ,t1.ZHJY2_0016
        ,t2.ZHJY2_0017_01
        ,t2.ZHJY2_0017_02
        ,t2.ZHJY2_0017_03
        ,t1.ZHJY2_0014-t2.ZHJY2_0017_03       as ZHJY2_0017
        ,t2.ZHJY2_0018_01
        ,t2.ZHJY2_0018_02
        ,t1.ZHJY2_0015-t2.ZHJY2_0018_02      as  ZHJY2_0018
        ,t2.ZHJY2_0019_01
        ,t2.ZHJY2_0019_02
