                                  AND     substr(dcst_1.prod_cd, 1, 6) IN ( '810101' , '810105' , '810108' , '810109' , '810110' )
                                  GROUP BY dcst_1.cust_id
                              ) t2
                 ON      t1.cust_id = t2.cust_id
                 LEFT JOIN    (
