            SELECT  zh_1_1.dt
                    ,zh_1_2.tem_org_id
                    ,SUM(CASE
                           WHEN substr(prod_cd, 1, 4) = '8101' THEN nvl(avg_bal, 0)
                           ELSE 0
                         END) AS loan_avg_bal
                    ,SUM(CASE
                           WHEN substr(prod_cd, 1, 4) = '8201' THEN nvl(avg_bal, 0)
                           ELSE 0
