        ,SUM(CASE
               WHEN T1.trg_jzdh_rule_type_cd IN ( '01' , '02' , '03' ) THEN 1
               ELSE 0
             END)                         AS 分子
        ,count(T1.cst_id)                 AS 分母
        ,SUM(CASE
               WHEN T1.trg_jzdh_rule_type_cd IN ( '01' , '02' , '03') THEN 1
               ELSE 0
             END) / count(T1.cst_id)      AS 触发率
