on t.socialcreditcode=gscst.gpr_creditcode_all



left join (

                  SELECT  t.cst_id



                  FROM    edw.dim_bus_loan_ctr_inf_dd t

                  WHERE   t.dt = '@@{yyyyMMdd}'

                  AND     ( t.pd_cd NOT IN ( '201050102010146' , '201050101040348' )   OR t.frz_sts_cd <> '' ) --剔除未签约的泰e贷

