									  OR ( ( nvl(t.crc_ind, '0') <> '0'   OR t.pd_cd LIKE '2010503%' ) AND     ( t.ctr_bal = 0  OR t.ctr_bal IS NULL )  AND     t.apnt_mtu_dt >= '@@{yyyyMMdd}' )

