        ,T1.CTR_BGN_DT    --约定开始日期

        ,T1.CTR_MTU_DT    --约定到期日期

		--,COALESCE(T7.BIND_LMT,T13.SEG_LMT,T1.ctr_amt,0) * T18.AVG_PRC / T18.QUO_UNT AS CYC_REALSUM -- 实际启用金额折人民币  新信贷改造删除T9.RESERVESUM

		,CASE

