           WHEN T1.RVLG_TYP_CD <> '1' THEN T1.CTR_AMT    --非循环贷款，取合同金额

           ELSE T1.STRT_USE_AMT     --启用金额

         END   as   ACTL_START_USE_CRD_LMT --启用额度

        ,T1.TMT_DT     --合同终结日期

        ,COALESCE(T14.COUNT_DBIL_ID, 0) COUNT_DBIL_ID   --累计发放借据数

        ,COALESCE(T14.SUM_AMT, 0) SUM_AMT    --累计发放金额

