ON      T1.CTR_SERNO = T16.CTR_SERNO

LEFT JOIN    edw.DIM_BUS_LOAN_PRD_FRML_DD T17 --信贷产品信息

ON      T1.PRD_NO = T17.PRD_NO

AND     T17.dt = '20250331'

