INSERT OVERWRITE TABLE lab_bigdata_dev.phjrb_ys_qytb_bus_loan_ctr_inf_all_new PARTITION (DT = '20250331')

SELECT  T0.ent_cst_id --企业客户号

        ,T0.ent_cst_nm --企业客户名称

        ,T0.prm_mgr_id --主管户客户经理工号

