                 GROUP BY cst_id

             ) T3

ON      T1.cst_id = T3.cst_id

left join (select   distinct  t1.cst_id

from lab_bigdata_dev.phjrb_ys_qytb_cst_info_tmp_2024_new   t1    ----2024年新增的消费贷款客户

