                    ,coalesce(sum(t1.aum_bal_add) OVER ( PARTITION BY T1.ent_cst_nm , T1.brc_org_nm ), 0) brc_aum_add --分行_企业aum_add

                    ,coalesce(sum(t1.aum_bal) OVER ( PARTITION BY T1.ent_cst_nm , T1.sbr_org_nm ), 0)     sbr_aum     --支行_企业aum

